import { Component, useState } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: [],
      searchText: "",
    };
  }
  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((userS) => {
        this.setState(
          () => {
            return { monsters: userS };
          },
          () => {
            console.log(this.state.monsters);
          }
        );
      });
  }
  //Cumtom methods
  onSearchChange = (event) => {

    const searchFieldText = event.target.value.toLowerCase();
    this.setState(() => {
      return { searchText: searchFieldText };
    });
  }
  render() {
    const onSearchChange = this;
    let fitleredMonstaers = this.state.monsters.filter((monster) => {
      console.log(this.state.searchText);
      return monster.name.toLowerCase().includes(this.state.searchText);
    });
    return (
      <div className="App">
        <input
          className="search-box"
          placeholder="Search"
          type="search"
          onChange={onSearchChange}
        />
        {fitleredMonstaers.map((monster) => {
          return (
            <div key={monster.id}>
              <h1>{monster.name}</h1>
            </div>
          );
        })}
      </div>
    );
  }
}

export default App;
